{
  "name": "api",
  "dependencies": {
    "express": "^4.18.2",
    "cors": "^2.8.5",
    "dotenv": "^16.4.5",
    "zod": "^3.22.4"
  },
  "devDependencies": {
    "typescript": "^5.3.3",
    "tsx": "^4.7.1",
    "@types/express": "^4.17.21",
    "@types/cors": "^2.8.17",
    "@types/node": "^20.11.20"
  }
}

{
  "compilerOptions": {
    "target": "ESNext",
    "module": "commonjs",
    "outDir": "./dist",
    "rootDir": "./src",
    "strict": true,
    "esModuleInterop": true,
    "skipLibCheck": true,
    "forceConsistentCasingInFileNames": true
  },
  "include": [
    "src/**/*"
  ]
}

import express from 'express';
import cors from 'cors';
import dotenv from 'dotenv';
import { featureRouter } from './routes/features';

dotenv.config();

const app = express();
const PORT = process.env.PORT || 4000;

app.use(cors());
app.use(express.json());

app.get('/health', (req, res) => res.json({ status: 'ok', timestamp: new Date() }));
app.use('/api', featureRouter);

app.listen(PORT, () => {
    console.log(` API Server running at http://localhost:${PORT}`);
});

import { Router } from 'express';
export const featureRouter = Router();


// Authentication endpoint
featureRouter.get('/authentication', (req, res) => {
    res.json({ 
        success: true,
        project: 'Generate code with a README file included.',
        feature: 'Authentication',
        data: {
            id: Math.random().toString(36).substr(2, 9),
            status: 'active',
            lastUpdated: new Date()
        }
    });
});


// User Profile endpoint
featureRouter.get('/user-profile', (req, res) => {
    res.json({ 
        success: true,
        project: 'Generate code with a README file included.',
        feature: 'User Profile',
        data: {
            id: Math.random().toString(36).substr(2, 9),
            status: 'active',
            lastUpdated: new Date()
        }
    });
});


// Dashboard Settings endpoint
featureRouter.get('/dashboard-settings', (req, res) => {
    res.json({ 
        success: true,
        project: 'Generate code with a README file included.',
        feature: 'Dashboard Settings',
        data: {
            id: Math.random().toString(36).substr(2, 9),
            status: 'active',
            lastUpdated: new Date()
        }
    });
});

{
  "name": "database",
  "dependencies": {
    "@prisma/client": "^5.10.2"
  },
  "devDependencies": {
    "prisma": "^5.10.2"
  }
}

generator client {
  provider = "prisma-client-js"
}

datasource db {
  provider = "postgresql"
  url      = env("DATABASE_URL")
}

model User {
  id        String   @id @default(uuid())
  email     String   @unique
  name      String?
  projects  Project[]
  createdAt DateTime @default(now())
}

model Project {
  id          String   @id @default(uuid())
  title       String
  description String
  userId      String
  user        User     @relation(fields: [userId], references: [id])
  createdAt   DateTime @default(now())
}


model Authentication {
  id        String   @id @default(uuid())
  details   Json
  status    String   @default("pending")
  createdAt DateTime @default(now())
}


model UserProfile {
  id        String   @id @default(uuid())
  details   Json
  status    String   @default("pending")
  createdAt DateTime @default(now())
}


model DashboardSettings {
  id        String   @id @default(uuid())
  details   Json
  status    String   @default("pending")
  createdAt DateTime @default(now())
}
